import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerHirer } from "../actions/authActions";
import classnames from "classnames";
import "./Register.css";

class HirerRegister extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      phone: "",
      password: "",
      password2: "",
      errors: {},
    };
  }
  componentDidMount() {
    // If logged in and user navigates to Register page, should redirect them to dashboard
    if (this.props.auth.isAuthenticated) {
      this.props.history.push("/hirer-dashboard");
    }
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  onChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    const newHirer = {
      name: this.state.name,
      phone: this.state.phone,
      password: this.state.password,
      password2: this.state.password2,
    };
    console.log(newHirer);

    this.props.registerHirer(newHirer, this.props.history);
  };

  render() {
    const { errors } = this.state;
    return (
      <div className="register">
        <div className="container">
          <div className="register-form">
            <div>
              <form
                className="worker-register-inner"
                onSubmit={this.onSubmit}
                style={{ textAlign: "center" }}
              >
                <img
                  src="logo.png"
                  style={{ width: "200px", height: "auto" }}
                  alt=""
                />
                <h1 style={{ marginBottom: "30px" }}>Register as Worker</h1>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <input
                        onChange={this.onChange}
                        value={this.state.phone}
                        className={classnames("form-control shadow", {
                          invalid: errors.phone,
                        })}
                        type="text"
                        placeholder="Enter Mobile Number"
                        error={errors.phone}
                        id="phone"
                        style={{ width: "350px" }}
                      />
                    </div>
                    <span className="red-text">{errors.phone}</span>

                    <div className="form-group">
                      <input
                        onChange={this.onChange}
                        value={this.state.password}
                        className={classnames("form-control shadow", {
                          invalid: errors.password,
                        })}
                        type="password"
                        placeholder="Password"
                        id="password"
                        style={{ width: "350px" }}
                      />
                    </div>
                    <span className="red-text">{errors.password}</span>

                    <div className="form-group">
                      <input
                        onChange={this.onChange}
                        value={this.state.password2}
                        className={classnames("form-control shadow", {
                          invalid: errors.password2,
                        })}
                        type="password"
                        placeholder="Confirm Password"
                        id="password2"
                        style={{ width: "350px" }}
                      />
                    </div>
                  </div>
                  <span className="red-text">{errors.password2}</span>

                  <div className="col-md-6">
                    <div className="form-group">
                      <input
                        onChange={this.onChange}
                        className={classnames("form-control shadow", {
                          invalid: errors.name,
                        })}
                        type="text"
                        name="name"
                        placeholder="Enter Your Name"
                        id="name"
                        style={{ width: "350px" }}
                        value={this.state.name}
                      />
                    </div>
                    <span className="red-text">{errors.name}</span>
                    {/* <div className="form-group">
                      <input
                        className="form-control shadow"
                        type="text"
                        name="address"
                        placeholder="Enter Your address"
                        id="address"
                        style={{ width: "350px" }}
                      />
                    </div> */}
                    <label>I'm good at</label>
                    {/* <div className="row">
                      <div className="col-md-6">
                        <div className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            id="carpenter"
                            value="Carpenter"
                          />
                          <label className="form-check-label">Carpenter</label>
                        </div>
                      </div>
                      <div className="col-md-6">
                        <input type="text" placeholder="Fee Per day" />
                      </div>
                    </div> */}
                    <br />
                    {/* <div className="row">
                      <div className="col-md-6">
                        <div className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            id="mason"
                          />
                          <label className="form-check-label">Mason</label>
                        </div>
                      </div>
                      <div className="col-md-6">
                        <input type="text" placeholder="Fee Per day" />
                      </div>
                    </div> */}
                  </div>
                </div>

                <div className="form-group">
                  <button
                    className="btn btn-primary login-button shadow"
                    style={{ width: "200px" }}
                  >
                    Register
                  </button>
                </div>
                <a href="/">Forgot Password?</a>
              </form>
            </div>
          </div>
          <div>
            <button href="/" className="btn btn-link">
              <h3>ALready Have an Account? Click Here to Login</h3>
            </button>
          </div>
        </div>
      </div>
    );
  }
}

HirerRegister.propTypes = {
  registerHirer: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
});

export default connect(mapStateToProps, { registerHirer })(
  withRouter(HirerRegister)
);
