import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class Work extends Component {
  state = {
    works: [],
    worker: [],
  };

  componentDidMount() {
    axios.get("/works").then((response) => {
      if (response.data.length > 0) {
        console.log(response.data);
        this.setState({
          works: response.data,
        });
      }
    });
  }

  renderWorks = (works) => {
    return (
      <div>
        {works.map((work, index) => {
          // const posterId = work.postedBy ? work.postedBy._id : "No Id";
          const posterName = work.postedBy ? work.postedBy.name : "";
          const posterPhone = work.postedBy ? work.postedBy.phone : "";
          // const posterProfPic = work.postedBy
          //   ? work.postedBy.profilePicture
          //   : "";

          return (
            <div className="card shadow-sm" key={index} id="work-card">
              <div className="card-body">
                <div className="row">
                  <div className="col-md-6 col-xs-6">
                    {/* <Link to={`/posts/${work._id}`}>
                      <h5 className="card-title">{post.postTitle}</h5>
                    </Link> */}
                    <i>
                      <p>{new Date(work.created).toDateString()}</p>
                    </i>
                  </div>
                  <div className="col-md-4 col-xs-4">
                    <Link
                      // to={`/profile/${posterId}`}
                      to="/hirer-dashboard"
                      style={{
                        textDecoration: "none",
                        color: "black",
                        fontFamily: "Lato",
                        textAlign: "right",
                        lineHeight: "40px",
                      }}
                    >
                      <p>{`${posterName} ${posterPhone}`}</p>
                    </Link>
                  </div>
                  {/* <div className="col-md-2 col-xs-4 col">
                    <img
                      src={posterProfPic}
                      alt=""
                      width="45px"
                      height="auto"
                      style={{
                        borderRadius: "50px",
                      }}
                    />
                  </div> */}
                </div>
                <p className="card-text">{work.title}</p>
                <img className="card-img-top" src={work.images[1]} alt="" />
              </div>
            </div>
          );
        })}
      </div>
    );
  };

  render() {
    const { works } = this.state;

    return <div className="container">{this.renderPosts(works)}</div>;
  }
}
export default Work;
