import React, { Component } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import { Link } from "react-router-dom";
import "./Home.css";

class Home extends Component {
  state = {
    modalIsOpen: false,
  };

  toggleModal = () => {
    this.setState({
      modalIsOpen: !this.state.modalIsOpen,
    });
  };

  redirectToWorker = () => {
    window.location = "/worker-register";
  };
  redirectToHirer = () => {
    window.location = "/hirer-register";
  };

  render() {
    return (
      <div className="home">
        <div className="container">
          <img
            src="/logo.png"
            alt="niwahana-logo"
            style={{ width: "50px", height: "auto" }}
          />
          <Link to="/hirer-login">
            <button className="btn btn-primary btn-lg">
              I want to work as a Hirer
            </button>
          </Link>

          <Link to="/worker-login">
            <button className="btn btn-primary btn-lg">
              I want to work as a Worker
            </button>
          </Link>

          <div>
            <button
              href="/"
              className="btn btn-link"
              onClick={this.toggleModal.bind(this)}
            >
              <h3>Create an Account?</h3>
            </button>
          </div>
          <Modal isOpen={this.state.modalIsOpen}>
            <ModalHeader toggle={this.toggleModal.bind(this)}>
              Sign Up
            </ModalHeader>
            <ModalBody>How do you want to sign up as?</ModalBody>
            <ModalFooter>
              <button
                className="btn btn-primary"
                onClick={this.redirectToHirer}
              >
                Sign as Hirer
              </button>
              <button
                className="btn btn-primary"
                onClick={this.redirectToWorker}
              >
                Sign as Worker
              </button>
            </ModalFooter>
          </Modal>
        </div>
      </div>
    );
  }
}

export default Home;
