import axios from "axios";
import setAuthToken from "../utills/setAuthToken";
import jwt_decode from "jwt-decode";
import {
  GET_ERRORS,
  SET_CURRENT_HIRER,
  SET_CURRENT_WORKER,
  HIRER_LOADING,
  WORKER_LOADING,
} from "./types";

// Register Worker
export const registerWorker = (workerData, history) => (dispatch) => {
  axios
    .post("/workers/register", workerData)
    .then((res) => history.push("/worker-login")) // re-direct to login on successful register
    .catch((err) =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data,
      })
    );
};

// Register Hirer
export const registerHirer = (hirerData, history) => (dispatch) => {
  axios
    .post("/hirers/register", hirerData)
    .then((res) => history.push("/hirer-login")) // re-direct to login on successful register
    .catch((err) =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data,
      })
    );
};

// Worker Login - get user token
export const loginWorker = (workerData) => (dispatch) => {
  axios
    .post("/workers/login", workerData)
    .then((res) => {
      // Save to localStorage
      // Set token to localStorage
      const { token } = res.data;

      localStorage.setItem("jwtToken", token);
      // Set token to Auth header
      setAuthToken(token);
      // Decode token to get user data
      const decodedWorker = jwt_decode(token);
      // Set current user
      console.log(decodedWorker);
      dispatch(setCurrentWorker(decodedWorker));
    })
    .catch((err) =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data,
      })
    );
};

// Set logged in user
export const setCurrentWorker = (decodedWorker) => {
  return {
    type: SET_CURRENT_WORKER,
    payload: decodedWorker,
  };
};
// User loading
export const setWorkerLoading = () => {
  return {
    type: WORKER_LOADING,
  };
};

// Hirer Login - get user token
export const loginHirer = (hirerData) => (dispatch) => {
  axios
    .post("/hirers/login", hirerData)
    .then((res) => {
      // Save to localStorage
      // Set token to localStorage
      const { token } = res.data;
      localStorage.setItem("jwtToken", token);
      // Set token to Auth header
      setAuthToken(token);
      // Decode token to get user data
      const decodedHirer = jwt_decode(token);
      // Set current user
      dispatch(setCurrentHirer(decodedHirer));
    })
    .catch((err) =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data,
      })
    );
};

// Set logged in user
export const setCurrentHirer = (decodedHirer) => {
  return {
    type: SET_CURRENT_HIRER,
    payload: decodedHirer,
  };
};
// User loading
export const setHirerLoading = () => {
  return {
    type: HIRER_LOADING,
  };
};

// Log worker out
export const logoutWorker = () => (dispatch) => {
  // Remove token from local storage
  localStorage.removeItem("jwtToken");
  // Remove auth header for future requests
  setAuthToken(false);
  // Set current user to empty object {} which will set isAuthenticated to false
  dispatch(setCurrentUser({}));
};

// Log hirer out
export const logoutHirer = () => (dispatch) => {
  // Remove token from local storage
  localStorage.removeItem("jwtToken");
  // Remove auth header for future requests
  setAuthToken(false);
  // Set current user to empty object {} which will set isAuthenticated to false
  dispatch(setCurrentUser({}));
};
