import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";

class CreateWork extends Component {
  state = {
    title: "",
    fee: 0,
    category: "",
    area: "",
    images: [],
    worker: {},
  };

  onChange = (event) => {
    this.setState({
      [event.target.id]: event.target.value,
    });
  };

  onChangeImages = (event) => {
    this.setState({
      images: event.target.files,
    });
  };

  onSubmit = (event) => {
    const { worker } = this.props.auth.user;
    event.preventDefault();
    const data = new FormData();
    data.append("title", this.state.title);
    data.append("fee", this.state.fee);
    data.append("category", this.state.category);
    data.append("area", this.state.area);
    for (var x = 0; x < this.state.images.length; x++) {
      data.append("file", this.state.images[x]);
    }
    data.append("worker", worker.id);

    console.log(data);
  };

  render() {
    return (
      <div className="card shadow">
        <div className="card-body">
          <form onSubmit={this.onSubmit}>
            <h5 className="card-title"> Create a new Work </h5>
            <div className="form-group" style={{ textAlign: "left" }}>
              <label>What can you do next?</label>
              <input
                type="text"
                class="form-control"
                id="title"
                placeholder="Ex:- I will do the wiring to your house"
                onChange={this.onChange}
              />
            </div>
            <div className="form-group">
              <div class="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  id="category"
                  value="Masonry"
                  checked={this.state.category === "Masonry"}
                  onChange={this.onChange}
                />
                <label className="form-check-label" for="inlineRadio1">
                  Masonry
                </label>
              </div>
              <div class="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  id="category"
                  value="Carpentry"
                  checked={this.state.category === "Carpentry"}
                  onChange={this.onChange}
                />
                <label className="form-check-label" for="inlineRadio2">
                  Carpentry
                </label>
              </div>
              <div class="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  id="category"
                  value="House Wiring"
                  checked={this.state.category === "House Wiring"}
                  onChange={this.onChange}
                />
                <label className="form-check-label" for="inlineRadio3">
                  House Wiring
                </label>
              </div>
              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  id="category"
                  value="Plumber"
                  checked={this.state.category === "Plumber"}
                  onChange={this.onChange}
                />
                <label class="form-check-label" for="inlineRadio2">
                  Plumber
                </label>
              </div>
              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  id="category"
                  value="House Painting"
                  checked={this.state.category === "House Painting"}
                  onChange={this.onChange}
                />
                <label className="form-check-label" for="inlineRadio2">
                  House Painting
                </label>
              </div>
              <br />
              <br />
              <div className="form-group" style={{ textAlign: "left" }}>
                <label>Fee Per Day(Rs.)</label>
                <input
                  type="number"
                  class="form-control"
                  id="fee"
                  placeholder="Ex:- 1000"
                  onChange={this.onChange}
                />
              </div>

              <div className="form-group" style={{ textAlign: "left" }}>
                <label>Area</label>
                <input
                  type="text"
                  class="form-control"
                  id="area"
                  placeholder="Ex:- Kuruwita"
                  onChange={this.onChange}
                />
              </div>

              <div className="form-group" style={{ textAlign: "left" }}>
                <label>Add Some Images</label>
                <input
                  type="file"
                  className=""
                  multiple
                  onChange={this.onChangeImages}
                />
              </div>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

CreateWork.propTypes = {
  auth: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
});
export default connect(mapStateToProps)(CreateWork);
