import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerWorker } from "../actions/authActions";
import classnames from "classnames";
import "./Register.css";

class WorkerRegister extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      phone: "",
      password: "",
      password2: "",
      errors: {},
    };
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  onChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    const newWorker = {
      name: this.state.name,
      phone: this.state.phone,
      password: this.state.password,
      password2: this.state.password2,
    };
    console.log(newWorker);

    this.props.registerWorker(newWorker, this.props.history);
  };

  render() {
    const { errors } = this.state;
    return (
      <div className="worker-register">
        <div className="container">
          <div className="worker-register-form">
            <div>
              <form className="worker-register-inner" onSubmit={this.onSubmit}>
                <img
                  src="logo.png"
                  style={{ width: "200px", height: "auto" }}
                  alt=""
                />
                <h1 style={{ marginBottom: "30px" }}>Register as Worker</h1>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <input
                        onChange={this.onChange}
                        value={this.state.phone}
                        className={classnames("form-control shadow", {
                          invalid: errors.name,
                        })}
                        type="text"
                        placeholder="Enter Mobile Number"
                        error={errors.phone}
                        id="phone"
                        style={{ width: "350px" }}
                      />
                    </div>
                    <div className="form-group">
                      <input
                        onChange={this.onChange}
                        value={this.state.password}
                        className="form-control shadow"
                        type="password"
                        placeholder="Password"
                        id="password"
                        style={{ width: "350px" }}
                      />
                    </div>
                    <div className="form-group">
                      <input
                        onChange={this.onChange}
                        value={this.state.password2}
                        className="form-control shadow"
                        type="password"
                        placeholder="Confirm Password"
                        id="password2"
                        style={{ width: "350px" }}
                      />
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="form-group">
                      <input
                        onChange={this.onChange}
                        className="form-control shadow"
                        type="text"
                        name="name"
                        placeholder="Enter Your Name"
                        id="name"
                        style={{ width: "350px" }}
                        value={this.state.name}
                      />
                    </div>
                    {/* <div className="form-group">
                      <input
                        className="form-control shadow"
                        type="text"
                        name="address"
                        placeholder="Enter Your address"
                        id="address"
                        style={{ width: "350px" }}
                      />
                    </div> */}
                    <label>I'm good at</label>
                    {/* <div className="row">
                      <div className="col-md-6">
                        <div className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            id="carpenter"
                            value="Carpenter"
                          />
                          <label className="form-check-label">Carpenter</label>
                        </div>
                      </div>
                      <div className="col-md-6">
                        <input type="text" placeholder="Fee Per day" />
                      </div>
                    </div> */}
                    <br />
                    {/* <div className="row">
                      <div className="col-md-6">
                        <div className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            id="mason"
                          />
                          <label className="form-check-label">Mason</label>
                        </div>
                      </div>
                      <div className="col-md-6">
                        <input type="text" placeholder="Fee Per day" />
                      </div>
                    </div> */}
                  </div>
                </div>

                <div className="form-group">
                  <button
                    className="btn btn-primary login-button shadow"
                    style={{ width: "200px" }}
                  >
                    Register
                  </button>
                </div>
                <a href="/">Forgot Password?</a>
              </form>
            </div>
          </div>
          <div>
            <button href="/" className="btn btn-link">
              <h3>ALready Have an Account? Click Here to Login</h3>
            </button>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
});

export default connect(mapStateToProps, { registerWorker })(
  withRouter(WorkerRegister)
);
