import React, { Component } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import "./Login.css";

class WorkerLogin extends Component {
  state = {
    email: "",
    password: "",
    errors: {},
    modalIsOpen: false,
  };

  toggleModal = () => {
    this.setState({
      modalIsOpen: !this.state.modalIsOpen,
    });
  };

  redirectToWorker = () => {
    window.location = "/worker-register";
  };
  redirectToHirer = () => {
    window.location = "/hirer-register";
  };

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();
    const workerData = {
      phone: this.state.phone,
      password: this.state.password,
    };
    console.log(workerData);
  };

  render() {
    const { errors } = this.state;
    return (
      <div className="login">
        <div className="container">
          <div className="login-form">
            <div>
              <form className="login-form-inner">
                <img
                  src="logo.png"
                  style={{ width: "200px", height: "auto" }}
                  alt=""
                />
                <h1 style={{ marginBottom: "30px" }}>Login as a Worker</h1>
                <div className="form-group">
                  <input
                    className="form-control shadow"
                    type="text"
                    id="phone"
                    placeholder="Mobile Number"
                  />
                </div>
                <div className="form-group">
                  <input
                    className="form-control shadow"
                    type="password"
                    id="password"
                    placeholder="Password"
                  />
                </div>
                <div className="form-group">
                  <button className="btn btn-primary login-button shadow">
                    Login
                  </button>
                </div>
                <a href="/">Forgot Password?</a>
              </form>
            </div>
          </div>
          <div>
            <button
              href="/"
              className="btn btn-link"
              onClick={this.toggleModal.bind(this)}
            >
              <h3>Create an Account?</h3>
            </button>
          </div>
          <Modal isOpen={this.state.modalIsOpen}>
            <ModalHeader toggle={this.toggleModal.bind(this)}>
              Sign Up
            </ModalHeader>
            <ModalBody>How do you want to sign up as?</ModalBody>
            <ModalFooter>
              <button
                className="btn btn-primary"
                onClick={this.redirectToHirer}
              >
                Sign as Hirer
              </button>
              <button
                className="btn btn-primary"
                onClick={this.redirectToWorker}
              >
                Sign as Worker
              </button>
            </ModalFooter>
          </Modal>
        </div>
      </div>
    );
  }
}

export default WorkerLogin;
